package week1.day2;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=10;

		for (i=1;i<=10;i++) {
			
			System.out.println(i);
			
			if (i%2==0)  {
				System.out.println("even"+i);
			}
				
				else {
					System.out.println("odd"+i);
				}
			
			
		}
		  
		
	
	}			

	
}
	
